import React from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import "./Book.scss";

const Book = (props) => {
    return (
        <Col lg="4">
            <Card className="card">
            <Card.Img className="card__img mb-4" src="https://mymagic-central.s3.amazonaws.com/uploads/organization/thumbnail/b3c6e2018d5590331561c27f3050ce5d3998c2a3.adaptiveResize.320x320.jpg" />

                <Card.Body>
                    <Card.Title className="card__title mb-2">{props.book.title}</Card.Title>
                    <Card.Subtitle className="card__author mb-4">{props.book.author}</Card.Subtitle>
                    <Card.Text className="card__description">{props.book.description}</Card.Text>
                </Card.Body>
                <Card.Footer>
                    <Button className="card__btn">Meer informatie</Button>
                </Card.Footer>
            </Card>
        </Col>
    )
}

export default Book;
