import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";

const BookEditForm = () => {
    const { id } = useParams();
    const [book, setBook] = useState({});

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`/book/${id}`, {
                    method: "GET"
                });
                const json = await response.json();
                setBook(json.result[0]);
            } catch (error) {
                console.log(error);
            }
        }

        fetchData();
    }, []);

    const handleChange = (event) => {
        const {name, value} = event.target;
        setBook((prevBook) => ({
            ...prevBook,
            [name]: value
        }));
    }

    const editBook = (event) => {
        event.preventDefault();
        fetch(`/book/${id}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(book)
        });
    }

    return (
        <>
            <Container>
                <Row>
                    <Form onSubmit={editBook}>
                        <Form.Group className="mb-3">
                            <Form.Label>Title</Form.Label>
                            <Form.Control value={book.title} name="title" placeholder="Enter title" onChange={handleChange} />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Description</Form.Label>
                            <Form.Control value={book.description} name="description" placeholder="Enter description" onChange={handleChange} />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Author</Form.Label>
                            <Form.Control value={book.author} name="author" placeholder="Enter author" onChange={handleChange} />
                        </Form.Group>
                        <Button variant="primary" type="submit">Submit</Button>
                    </Form>
                </Row>
            </Container>
        </>
    );
}

export default BookEditForm;
