import React from "react";
import { useHistory } from "react-router-dom";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import "./Book.scss";

const Book = (props) => {
    const history = useHistory();

    const editBook = () => {
        const path = "/edit"; 
        history.push(path);
    }
    
    const deleteBook = () => {
        try {
            fetch("/book/" + props.book.id, {
                method: "DELETE"
            });
            props.setBooks((prevBooks) => {
                const newBooks = {
                    ...prevBooks
                }
                delete newBooks[props.key];
                return newBooks;
            });
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <Col lg="3">
            <Card>
                <Card.Body>
                    <Card.Title>{props.book.title}</Card.Title>
                    <Card.Subtitle>{props.book.author}</Card.Subtitle>
                    <Card.Text>{props.book.description}</Card.Text>
                    <Button className="m-1" onClick={editBook}>Edit</Button>
                    <Button onClick={deleteBook}>Verwijderen</Button>
                </Card.Body>
            </Card>
        </Col>
    )
}

export default Book;
