import React, {useState} from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";

const BookCreateForm = () => {
    const [book, setBook] = useState({
        "title": "",
        "description": "",
        "author": ""
    });

    const handleChange = (event) => {
        const {name, value} = event.target;
        setBook((prevBook) => ({
            ...prevBook,
            [name]: value
        }));
    }

    const createNewBook = async () => {
        const response = await fetch("/book", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                "title": book.title,
                "description": book.description,
                "author": book.author
            })
        });
        const json = await response.json();
        console.log(json)
    }

    return (
        <>
            <Container>
                <Row>
                    <Form onSubmit={createNewBook}>
                        <Form.Group className="mb-3">
                            <Form.Label>Title</Form.Label>
                            <Form.Control value={book.title} name="title" placeholder="Enter title" onChange={handleChange} />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Description</Form.Label>
                            <Form.Control value={book.description} name="description" placeholder="Enter description" onChange={handleChange} />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Author</Form.Label>
                            <Form.Control value={book.author} name="author" placeholder="Enter author" onChange={handleChange} />
                        </Form.Group>
                        <Button variant="primary" type="submit">Submit</Button>
                    </Form>
                </Row>
            </Container>
        </>
    );
}

export default BookCreateForm;
