import React, {useState} from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";

const BookCreateForm = () => {
    const [book, setBook] = useState({
        "title": "",
        "description": "",
        "author": ""
    });

    const createNewBook = () => {
        try {
            fetch("/book", {
                method: "POST",
                body: {
                    "title": "",
                    "description": "",
                    "author": ""
                }
            });
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <>
            <Container>
                <Row>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>Title</Form.Label>
                            <Form.Control value={book.title} name="title" placeholder="Enter title" readOnly/>
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Description</Form.Label>
                            <Form.Control value={book.description} name="description" placeholder="Enter description" readOnly/>
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Author</Form.Label>
                            <Form.Control value={book.author} name="author" placeholder="Enter author" readOnly/>
                        </Form.Group>
                        <Button variant="primary" type="submit">Submit</Button>
                    </Form>
                </Row>
            </Container>
        </>
    );
}

export default BookCreateForm;
