import React, {useState, useEffect} from "react";
import Card from "../../books/components/card/Card";

const BooksOverview = () => {
    const [books, setBooks] = useState({
        title: "",
        description: "",
        author: ""
    });

    useEffect(() => {
        const fetchData = async () => {
            const API_URL = "http://localhost:3001/book"
            try {
                const response = await fetch(API_URL);
                console.log(response)
                const json = response.json();

                setBooks(json);
            } catch (error) {
                console.log(error);
            }
        }

        fetchData();
    }, []);

    return (
        <>
            <Card 
                allBooks={books}
            />
        </>
    );
}

export default BooksOverview;
