import React from "react";
import { Link } from "react-router-dom";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";
import Button from "../../shared/components/elements/button/Button";
import "./Book.scss";

const Book = (props, { bookKey }) => {
    const deleteBook = () => {
        try {
            fetch(`/book/${props.book.id}`, {
                method: "DELETE"
            });
            props.setBooks((prevBooks) => {
                const newBooks = {
                    ...prevBooks
                }
                delete newBooks[bookKey];
                return newBooks;
            });
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <Col lg="4">
            <Card>
                <Card.Body>
                    <Card.Title>{props.book.title}</Card.Title>
                    <Card.Subtitle>{props.book.author}</Card.Subtitle>
                    <Card.Text>{props.book.description}</Card.Text>
                    <Button 
                        route="/createBook"
                        title="Aanmaken"
                    />
                    <Link to={`/editBook/${props.book.id}`}><Button>Bewerken</Button></Link>
                    <Button onClick={deleteBook}>Verwijderen</Button>
                </Card.Body>
            </Card>
        </Col>
    )
}

export default Book;
