import React from "react";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";
import Link from "react-bootstrap/Link";
import Button from "react-bootstrap/Button";
import "./Book.scss";

const Book = (props) => {
    const editBook = () => {
        
    }
    
    const deleteBook = () => {
        try {
            fetch("/book/" + props.book.id, {
                method: "DELETE"
            });
            props.setBooks((prevBooks) => {
                const newBooks = {
                    ...prevBooks
                }
                delete newBooks[props.key];
                return newBooks;
            });
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <Col lg="3">
            <Card>
                <Card.Body>
                    <Card.Title>{props.book.title}</Card.Title>
                    <Card.Subtitle>{props.book.author}</Card.Subtitle>
                    <Card.Text>{props.book.description}</Card.Text>
                    <Link to="/edit">Edit</Link>
                    <Button onClick={deleteBook}>Verwijderen</Button>
                </Card.Body>
            </Card>
        </Col>
    )
}

export default Book;
