import React, {useState} from "react";
import { useHistory } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";

const BookCreateForm = () => {
    let history = useHistory();

    const [book, setBook] = useState({
        "title": "",
        "description": "",
        "author": ""
    });

    const handleChange = (event) => {
        const {name, value} = event.target;
        setBook((prevBook) => ({
            ...prevBook,
            [name]: value
        }));
    }

    const createNewBook = () => {
        try {
            fetch("/book", {
                method: "POST",
                body: {
                    "title": book.title,
                    "description": book.description,
                    "author": book.author
                }
            });
            history.push("/");
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <>
            <Container>
                <Row>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>Title</Form.Label>
                            <Form.Control value={book.title} name="title" placeholder="Enter title" onChange={handleChange} />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Description</Form.Label>
                            <Form.Control value={book.description} name="description" placeholder="Enter description" onChange={handleChange} />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Author</Form.Label>
                            <Form.Control value={book.author} name="author" placeholder="Enter author" onChange={handleChange} />
                        </Form.Group>
                        <Button variant="primary" type="submit" onClick={createNewBook}>Submit</Button>
                    </Form>
                </Row>
            </Container>
        </>
    );
}

export default BookCreateForm;
